function Complaint() {
  const classes = useStyles();
  const complainCategory = [
    {
      title: "Information Update",
    },
    {
      title: "Payment and subscription",
    },
    {
      title: "Broken interface",
    },
    {
      title: "Content related issue",
    },
    {
      title: "Issue after update",
    },
    {
      title: "Other",
    },
  ];

  const [category, setCategory] = useState(complainCategory[0].title);
  const [actionID, setActionID] = useState("");
  const [disable, setDisable] = useState(false);

  const { REACT_APP_BACKEND: BACKEND } = process.env;

  const [snackData, setSnackData] = useState({
    type: "",
    message: "",
    open: false,
  });

  const [myComplaints, setMyComplaints] = useState([]);
  const [pageLoading, setPageLoading] = useState(true);

  //   Edit Feedback Modal methods
  const [editComplaint, seteditComplaint] = useState(false);

  const editOpen = () => {
    seteditComplaint(true);
  };

  const editClose = () => {
    seteditComplaint(false);
  };

  const [deleteComplaint, setdeleteComplaint] = useState(false);

  const deleteOpen = () => {
    setdeleteComplaint(true);
  };

  const deleteClose = () => {
    setdeleteComplaint(false);
  };

  const {
    register: register1,
    formState: { errors: errors1 },
    handleSubmit: handleSubmit1,
    setValue,
  } = useForm();

  const {
    register,
    formState: { errors },
    handleSubmit,
    reset,
  } = useForm();

  function getmyComplaints() {
    var config = {
      method: "get",
      url: `${BACKEND}/userRouter/viewAllComplaints${
        JSON.parse(localStorage.getItem("user"))._id
      }`,
      headers: {
        Authorization: localStorage.getItem("token"),
        "Content-Type": "application/json",
      },
    };

    axios(config)
      .then(function (response) {
        if (response.data !== "No Complaints register.")
          setMyComplaints(response.data);
        setPageLoading(false);
      })
      .catch(function (error) {
        console.log(error);
        setSnackData({
          type: "error",
          message: "Error getting Complaints",
          open: true,
        });
      });
  }

  function postComplaint(data) {
    console.log(data);
    setSnackData({
      type: "error",
      message: "",
      open: false,
    });
    setDisable(true);
    var config = {
      method: "post",
      url: `${BACKEND}/userRouter/registerComplaint${
        JSON.parse(localStorage.getItem("user"))._id
      }`,
      headers: {
        Authorization: localStorage.getItem("token"),
        "Content-Type": "application/json",
      },
      data: {
        category: category,
        description: data.complaint,
      },
    };

    axios(config)
      .then(function (response) {
        setMyComplaints([response.data, ...myComplaints]);
        setDisable(false);
        setSnackData({
          type: "success",
          message: "Complaint posted successfully",
          open: true,
        });
        reset();
      })
      .catch(function (error) {
        console.log(error);
        setDisable(false);
        setSnackData({
          type: "error",
          message: "Error posting complaint",
          open: true,
        });
      });
  }

  useEffect(() => {
    getmyComplaints();
  }, []);

  return (
    <div className={classes.content}>
      <Snackbar
        open={snackData.open}
        type={snackData.type}
        message={snackData.message}
      />
      <Typography variant="h6" component={"h1"} className={classes.heading}>
        COMPLAINTS AND RESPONSES
      </Typography>
      <Typography
        variant="h6"
        component={"h2"}
        className={classes.ratingFeedbackHead}
      >
        Your Complaint
      </Typography>
      <Card className={classes.complaintCard}>
        <Typography
          variant="h6"
          component={"h6"}
          className={classes.catagoryHeading}
        >
          Select Category
        </Typography>
        <Autocomplete
          id="combo-box-demo"
          options={complainCategory}
          getOptionLabel={(option) => option.title}
          defaultValue={complainCategory[0]}
          style={{ width: 300 }}
          onChange={(e, val) => setCategory(val.title)}
          renderInput={(params) => (
            <TextField
              {...params}
              variant="outlined"
              className={classes.complainCategory}
              InputLabelProps={{ shrink: true }}
              required
            />
          )}
        />

        <Typography
          variant="h6"
          component={"h6"}
          className={classes.complaintHeading}
        >
          Complaint Detail
        </Typography>
        <form
          noValidate
          onSubmit={handleSubmit((data) => {
            postComplaint(data);
          })}
        >
          <TextField
            variant={"outlined"}
            multiline
            fullWidth
            rows={8}
            InputLabelProps={{ shrink: true }}
            {...register("complaint", {
              required: "Complaint Detail Required",
              pattern: {
                value: /^[a-zA-Z0-9_. -]{2,}$/,
                message: "Invalid ComplaintDetail",
              },
            })}
            error={Boolean(errors.complaint)}
            helperText={errors.complaint?.message}
          />
          <div className={classes.dFlexBetween}>
            <Button
              className={classes.submitBtn}
              variant={"contained"}
              type="submit"
              disableElevation
              disabled={disable}
              color={"primary"}
            >
              Submit Complaint
              {disable ? <Progress color="primary" size={20} /> : ""}
            </Button>
          </div>
        </form>
      </Card>
      <Typography
        variant="h6"
        component={"h2"}
        className={classes.ratingsFeedbacksHeading}
      >
        Your Complaints
      </Typography>
      <div className={classes.ratingsFeedbacksContainer}>
        {myComplaints.length > 0 ? (
          myComplaints.map((ele) => {
            return (
              <Card className={classes.feedbackCard}>
                <div className={classes.dFlexBetween}>
                  <div>
                    <Typography
                      variant="h6"
                      component={"h3"}
                      className={classes.cardName}
                    >
                      {ele.userFullName}
                    </Typography>
                    <Rating
                      name="simple-controlled"
                      size="small"
                      value={ele.review}
                      disabled
                      className={classes.cardRating}
                    />
                  </div>
                  <div>
                    <IconButton
                      color="primary"
                      aria-label="edit"
                      onClick={() => {
                        setActionID(ele._id);
                        setValue("feedback", ele.feedback);
                        editOpen();
                      }}
                    >
                      <MdEdit color="primary" />
                    </IconButton>
                    <IconButton
                      color="primary"
                      aria-label="delete"
                      onClick={() => {
                        setActionID(ele._id);
                        deleteOpen();
                      }}
                    >
                      <MdDelete />
                    </IconButton>
                  </div>
                </div>
                <Typography
                  variant="body1"
                  component={"p"}
                  className={classes.cardFeedback}
                >
                  {ele.feedback}
                </Typography>
                <div className={classes.reply}>
                  {ele.reply ? (
                    <div className={classes.p2}>
                      <Typography
                        variant="body1"
                        component={"p"}
                        color="primary"
                        className={classes.cardFeedback}
                      >
                        Admin
                      </Typography>
                      <Typography variant="body1" component={"p"}>
                        {ele.reply}
                      </Typography>
                    </div>
                  ) : (
                    ""
                  )}
                </div>
                <Typography
                  variant="body1"
                  component={"p"}
                  className={classes.cardTag}
                >
                  Powered By IntelliGenie
                </Typography>
              </Card>
            );
          })
        ) : (
          <div className={classes.displayFlexCenter}>
            {pageLoading ? (
              <CircularProgress size={30} color="primary" />
            ) : (
              <p>No Complaints To Show</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default Complaint;
