import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import StepContent from "@material-ui/core/StepContent";
import Button from "@material-ui/core/Button";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import { TextField, Box } from "@material-ui/core";
import SignInUpTemplate from "./SignInUpTemplate";
import { useState } from "react";
import { useForm } from "react-hook-form";
import Progress from "./ButtonLoader";
import { useRef } from "react";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
  },
  button: {
    marginTop: theme.spacing(1),
    marginRight: theme.spacing(1),
  },
  actionsContainer: {
    marginBottom: theme.spacing(2),
  },
  resetContainer: {
    padding: theme.spacing(3),
  },
  inputFeild: {
    maxWidth: 400,
  },
  content: {
    marginTop: theme.spacing(5),
    marginBottom: theme.spacing(5),
  },
}));

function getSteps() {
  return ["Verify Email", "Verify OTP", "Change Password"];
}

export default function ForgetPassword() {
  const classes = useStyles();
  function getStepContent(step) {
    switch (step) {
      case 0:
        return (
          <form
            noValidate
            onSubmit={handleSubmit((data) => {
              // signIn(data);
            })}
          >
            <TextField
              variant={"outlined"}
              InputLabelProps={{ shrink: true }}
              size={"medium"}
              color={"primary"}
              label={"Email"}
              name="userEmail"
              className={classes.inputFeild}
              {...register("userEmail", {
                required: "Email required",
                pattern: {
                  value:
                    /^[a-zA-Z]+[a-zA-Z0-9_.-]{1,}\@([A-Za-z0-9_\-\.]){1,}\.([A-Za-z]){2,4}$/,
                  message: "Invalid email",
                },
              })}
              error={Boolean(errors.userEmail)}
              helperText={errors.userEmail?.message}
              fullWidth
            ></TextField>
            <Box className={classes.btnGroup}>
              <Box className={classes.button}>
                <Button
                  variant={"contained"}
                  disableElevation
                  color="primary"
                  type="submit"
                  size="sm"
                  disabled={disable}
                >
                  Verify Email
                  {disable ? <Progress size={20} /> : ""}
                </Button>
              </Box>
            </Box>
          </form>
        );
      case 1:
        return (
          <form
            noValidate
            onSubmit={handleSubmit1((data) => {
              // signIn(data);
            })}
          >
            <TextField
              variant={"outlined"}
              InputLabelProps={{ shrink: true }}
              size={"medium"}
              color={"primary"}
              label={"OTP"}
              name="otp"
              className={classes.inputFeild}
              {...register1("otp", {
                required: "OTP required",
                pattern: {
                  value: /^[0-9]{4}$/,
                  message: "Invalid OTP",
                },
              })}
              error={Boolean(errors1.otp)}
              helperText={errors1.otp?.message}
              fullWidth
            ></TextField>
            <Box className={classes.btnGroup}>
              <Box className={classes.button}>
                <Button
                  variant={"contained"}
                  disableElevation
                  color="primary"
                  type="submit"
                  size="sm"
                  disabled={disable}
                >
                  Verify OTP
                  {disable ? <Progress size={20} /> : ""}
                </Button>
              </Box>
            </Box>
          </form>
        );
      case 2:
        return (
          <form
            noValidate
            onSubmit={handleSubmit1((data) => {
              //   updatePassword(data);
            })}
            className={classes.passwordSection}
          >
            <TextField
              variant={"outlined"}
              InputLabelProps={{ shrink: true }}
              size={"medium"}
              color={"primary"}
              label={"New Password"}
              className={classes.inputFeild}
              name="password"
              {...register1("password", {
                required: "Password Required",
                minLength: { value: 6, message: "Invalid Password" },
              })}
              type={"password"}
              fullWidth
              error={Boolean(errors1.password)}
              helperText={errors1.password?.message}
            ></TextField>
            <TextField
              variant={"outlined"}
              InputLabelProps={{ shrink: true }}
              size={"medium"}
              color={"primary"}
              label={"Confirm Password"}
              className={classes.inputFeild}
              name="confirmPassword"
              {...register1("confirmPassword", {
                required: "Confirm Password Required",
                validate: (value) =>
                  value === password.current || "Passwords do not match",
              })}
              type={"password"}
              error={Boolean(errors1.confirmPassword)}
              helperText={errors1.confirmPassword?.message}
              fullWidth
            ></TextField>
            <Button
              color="primary"
              variant={"contained"}
              type="submit"
              disabled={passwordLoading}
            >
              Update Password {passwordLoading ? <Progress size={20} /> : ""}
            </Button>
          </form>
        );
      default:
        return "Unknown step";
    }
  }
  const {
    register,
    formState: { errors },
    handleSubmit,
  } = useForm();

  const {
    register: register1,
    formState: { errors: errors1 },
    handleSubmit: handleSubmit1,
  } = useForm();

  const {
    register: register2,
    formState: { errors: errors2 },
    handleSubmit: handleSubmit2,
    watch,
  } = useForm();

  const password = useRef({});
  password.current = watch("password", "");

  const [disable, setDisable] = useState(false);
  const [activeStep, setActiveStep] = React.useState(1);
  const steps = getSteps();

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleReset = () => {
    setActiveStep(0);
  };

  return (
    <SignInUpTemplate>
      <div className={classes.root}>
        <Stepper activeStep={activeStep} orientation="vertical">
          {steps.map((label, index) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
              <StepContent className={classes.content}>
                <Typography>{getStepContent(index)}</Typography>
              </StepContent>
            </Step>
          ))}
        </Stepper>
        {activeStep === steps.length && (
          <Paper square elevation={0} className={classes.resetContainer}>
            <Typography>All steps completed - you&apos;re finished</Typography>
            <Button onClick={handleReset} className={classes.button}>
              Reset
            </Button>
          </Paper>
        )}
      </div>
    </SignInUpTemplate>
  );
}
