    },

    [theme.breakpoints.down("sm")]: {
      margin: theme.spacing(2),
    },
  },
  heading: {
    textAlign: "center",
    color: theme.palette.primary.main,
    fontWeight: "bold",
    fontSize: "1.5rem",
    marginBottom: theme.spacing(4),
  },
  ratingFeedbackHead: {
    padding: theme.spacing(1),
    color: theme.palette.primary.main,
  },
  ratingFeedbackCard: {
    padding: theme.spacing(4),
    margin: theme.spacing(1),
    boxSizing: "border-box",
    boxShadow: "none",
    border: `1px solid ${theme.palette.primary.light}`,
    width: "98.5%",
    [theme.breakpoints.down("xs")]: {
      width: "95%",
      padding: theme.spacing(3),
    },
  },
  ratingHeading: {
    fontSize: "14px",
    marginBottom: theme.spacing(1),
    fontWeight: "bold",
    color: theme.palette.dark.main,
  },
  ratingStars: {
    transform: "scale(1.5)",
  },
  feedbackHeading: {
    color: theme.palette.dark.main,
    fontSize: "14px",
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2),
    fontWeight: "bold",
  },
  submitBtn: {
    marginTop: theme.spacing(2),
    color: theme.palette.light.main,
    paddingRight: theme.spacing(2),
    paddingLeft: theme.spacing(2),
  },
  btn: {
    marginTop: theme.spacing(2),
    paddingRight: theme.spacing(2),
    paddingLeft: theme.spacing(2),
  },
  ratingsFeedbacksHeading: {
    marginTop: theme.spacing(4),
    padding: theme.spacing(1),
    color: theme.palette.primary.main,
  },
  editRatingsFeedbacksHeading: {
    marginBottom: theme.spacing(4),
    color: theme.palette.primary.main,
    textAlign: "center",
  },
  ratingsFeedbacksContainer: {
    maxWidth: "100%",
    display: "flex",
    flexWrap: "wrap",
  },
  feedbackCard: {
    flexGrow: 1,
    width: "300px",
    minHeight: "200px",
    boxShadow: "none",
    margin: theme.spacing(1),
    padding: theme.spacing(4),
    border: `1px solid ${theme.palette.primary.light}`,
  },
  cardName: {
    fontSize: "16px",
    fontWeight: "bold",
    color: theme.palette.primary.main,
  },
  cardRating: {
    marginTop: theme.spacing(1),
  },
  cardFeedback: {
    marginTop: theme.spacing(1),
  },
  cardTag: {
    display: "inline-block",
    fontWeight: "bold",
    fontFamily: "Cookie, cursive",
    fontSize: "20px",
    marginTop: theme.spacing(2),
    backgroundImage: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
    backgroundSize: "100%",
    WebkitBackgroundClip: "text",
    MozBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    MozTextFillColor: "transparent",
  },
  dFlexBetween: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "start",
  },

  ml3: {
    marginLeft: theme.spacing(3),
  },
  paper: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    backgroundColor: theme.palette.background.paper,
    borderRadius: theme.shape.borderRadius,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(5),
    paddingLeft: theme.spacing(5),
    paddingRight: theme.spacing(5),
    width: "600px",
    [theme.breakpoints.down("sm")]: {
      padding: theme.spacing(5),
      paddingLeft: theme.spacing(3),
      paddingRight: theme.spacing(3),
    },
  },
  modalForm: {
    width: "100%",
  },
  modal: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  deleteIcon: {
    backgroundColor: theme.palette.danger.main,
  },
  btnDanger: {
    backgroundColor: theme.palette.danger.main,
    color: theme.palette.light.main,
    "&:hover": {
      backgroundColor: alpha(theme.palette.danger.main, 0.8),
    },
    "&:disable": {
      backgroundColor: alpha(theme.palette.primary.main, 0.8),
    },
  },
  displayFlex: {
    display: "flex",
    alignItems: "center",
    marginBottom: theme.spacing(2),
  },
  dangerHeading: {
    marginLeft: theme.spacing(1),
  },
  contentPart1: {
    marginBottom: theme.spacing(2),
  },
}));

function RatingFeedback() {
  const { REACT_APP_BACKEND: BACKEND } = process.env;
  const classes = useStyles();
  const [rating, setRating] = useState(0);
  const [feedback, setFeedback] = useState("");
  const [disable, setDisable] = useState(false);

  const [editRating, setEditRating] = useState(0);
  const [editFeedbackVal, setEditFeedbackVal] = useState("");
  const [actionDisable, setActionDisable] = useState(false);

  const [snackData, setSnackData] = useState({
    type: "",
    message: "",
    open: false,
  });

  //   Edit Feedback Modal methods
  const [editFeedback, seteditFeedback] = useState(false);

  const editOpen = () => {
    seteditFeedback(true);
  };

  const editClose = () => {
    seteditFeedback(false);
  };

  const [deleteFeedback, setdeleteFeedback] = useState(false);

  const deleteOpen = () => {
    setdeleteFeedback(true);
  };

  const deleteClose = () => {
    setdeleteFeedback(false);
  };

  const {
    register: register1,
    formState: { errors: errors1 },
    handleSubmit: handleSubmit1,
    setValue,
  } = useForm();

  const {
    register,
    formState: { errors },
    handleSubmit,
    reset,
  } = useForm();

  function postFeedback(data) {
    console.log(data);
    setSnackData({
      type: "error",
      message: "",
      open: false,
    });
    setDisable(true);
    var config = {
      method: "post",
      url: `${BACKEND}/userRouter/provideReviewAndFeedback${
        JSON.parse(localStorage.getItem("user"))._id
      }`,
      headers: {
        Authorization: localStorage.getItem("token"),
        "Content-Type": "application/json",
      },
      data: {
        review: rating,
        feedback: data.feedback,
      },
    };

    axios(config)
      .then(function (response) {
        console.log(JSON.stringify(response.data));
        setDisable(false);
        setSnackData({
          type: "success",
          message: "Feedback posted successfully",
          open: true,
        });
        reset();
      })
      .catch(function (error) {
        console.log(error);
        setDisable(false);
        setSnackData({
          type: "error",
          message: "Error posting feedback",
          open: true,
        });
      });
  }

  return (
    <div className={classes.content}>
      <Snackbar
        open={snackData.open}
        type={snackData.type}
        message={snackData.message}
      />
      <Typography variant="h6" component={"h1"} className={classes.heading}>
        RATINGS AND FEEDBACKS
      </Typography>
      <Typography
        variant="h6"
        component={"h2"}
        className={classes.ratingFeedbackHead}
      >
        Your Feedback
      </Typography>
      <Card className={classes.ratingFeedbackCard}>
        <Typography
          variant="h6"
          component={"h6"}
          className={classes.ratingHeading}
        >
          Please Rate Us
        </Typography>
        <Rating
          name="simple-controlled"
          size="large"
          value={rating}
          onChange={(_, val) => setRating(val)}
        />
        <Typography
          variant="h6"
          component={"h6"}
          className={classes.feedbackHeading}
        >
          Please Provide Feedback
        </Typography>
        <form
          noValidate
          onSubmit={handleSubmit((data) => {
            postFeedback(data);
          })}
        >
          <TextField
            variant={"outlined"}
            multiline
            fullWidth
            rows={8}
            InputLabelProps={{ shrink: true }}
            {...register("feedback", {
              required: "Feedback Required",
              pattern: {
                value: /^[a-zA-Z0-9_. -]{2,}$/,
                message: "Invalid Feedback",
              },
            })}
            error={Boolean(errors.feedback)}
            helperText={errors.feedback?.message}
          />
          <div className={classes.dFlexBetween}>
            <Button
              className={classes.submitBtn}
              variant={"contained"}
              type="submit"
              disabled={disable}
              color={"primary"}
            >
              Submit Feedback
              {disable ? <Progress color="primary" size={20} /> : ""}
            </Button>
          </div>
        </form>
      </Card>
      <Typography
        variant="h6"
        component={"h2"}
        className={classes.ratingsFeedbacksHeading}
      >
        Your Feedbacks
      </Typography>
      <div className={classes.ratingsFeedbacksContainer}>
        <Card className={classes.feedbackCard}>
          <div className={classes.dFlexBetween}>
            <div>
              <Typography
                variant="h6"
                component={"h3"}
                className={classes.cardName}
              >
                Muhammad Yousaf
              </Typography>
              <Rating
                name="simple-controlled"
                size="small"
                value={5}
                disabled
                className={classes.cardRating}
              />
            </div>
            <div>
              <IconButton aria-label="delete" onClick={editOpen}>
                <MdEdit />
              </IconButton>
              <IconButton aria-label="delete" onClick={deleteOpen}>
                <MdDelete />
              </IconButton>
            </div>
          </div>
          <Typography
            variant="body1"
            component={"p"}
            className={classes.cardFeedback}
          >
            Adrien Brody and Milla Jovovich, "Dummy" is that special kind of
            indie comedy that knows how to perfectly balance its offbeat humor
            with just the right amount of dramatic intrigue.
          </Typography>
          <Typography
            variant="body1"
            component={"p"}
            className={classes.cardTag}
          >
            Powered By IntelliGenie
          </Typography>
        </Card>
      </div>
      <Typography
        variant="h6"
        component={"h2"}
        className={classes.ratingsFeedbacksHeading}
      >
        Other People Feedbacks
      </Typography>
