import {
  Accordion,
  AccordionSummary,
  AccordionDetails,
  makeStyles,
  Button,
  Typography,
  Tooltip,
  CircularProgress,
} from "@material-ui/core";
import {
  FiClipboard as ClipboardIcon,
  FiFacebook as FacebookIcon,
  FiTwitter as TwitterIcon,
} from "react-icons/fi";
import { FaWhatsapp as WhatsappIcon } from "react-icons/fa";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import CopyToClipboard from "react-copy-to-clipboard";
import { useEffect, useState } from "react";

const useStyles = makeStyles((theme) => ({
  content: {
    width: "100%",
    margin: theme.spacing(8),
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),

    [theme.breakpoints.down("md")]: {
      margin: theme.spacing(7),
      width: "100%",
    },

    [theme.breakpoints.down("sm")]: {
      margin: theme.spacing(2),
    },
  },
  heading: {
    textAlign: "center",
    color: theme.palette.primary.main,
    fontWeight: "bold",
    fontSize: "1.5rem",
    marginBottom: theme.spacing(4),
  },
  accordion: {
    paddingTop: theme.spacing(1),
    paddingBottom: theme.spacing(1),
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
    boxShadow: "none",
    border: `1px solid ${theme.palette.primary.light}`,
    borderRadius: theme.shape.borderRadius,
  },
  accordionHeading: {
    color: theme.palette.dark.main,
    fontWeight: "bold",
    fontSize: "18px",
  },
  contentHeading: {
    color: theme.palette.primary.main,
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2),
    fontWeight: "bold",
  },
  clipboardBtn: {
    borderRadius: "50%",
    marginRight: theme.spacing(2),
    marginBottom: theme.spacing(2),
    height: "65px",
    width: "65px",
    backgroundColor: theme.palette.primary.light,
    color: theme.palette.primary.main,
    boxShadow: "none",
    border: `1px solid ${theme.palette.primary.main}`,
    "&:hover": {
      backgroundColor: theme.palette.primary.light,
    },
  },
  btnIcon: {
    fontSize: "22px",
    padding: 0,
  },
  shareBtns: {
    display: "flex",
  },
  whatsappicon: {
    fontSize: "26px",
    padding: 0,
  },
}));

function FAQsShareApp() {
  const classes = useStyles();

  // Accordian
  const [expanded, setExpanded] = useState("panel1");
  const [faqs, setFaqs] = useState([]);

  const handleChange = (panel) => (event, isExpanded) => {
    setExpanded(isExpanded ? panel : false);
  };

  function getFAQS() {
    var config = {
      method: "get",
      url: "https://intelligenie-backend.herokuapp.com/userRouter/viewALLFAQs",
      headers: {
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VybmFtZSI6InVzYW1hMTIzNCIsImlkIjoiNjI2ZGE0ODAxYTBkMTAzYWM0NTkyODRjIiwiaWF0IjoxNjUxNDI2MzcxLCJleHAiOjE2ODc0MjYzNzF9.I8PrCsdmwCPSgtEtVP-UjRaephI6xq8UN1bmNtUY9Eg",
        "Content-Type": "application/json",
      },
      data: data,
    };

    axios(config)
      .then(function (response) {
        setFaqs(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  useEffect(() => {
    getFAQS();
  }, []);
  return (
    <div className={classes.content}>
      <Typography variant="h2" component={"h2"} className={classes.heading}>
        FAQS AND SHARE APP
      </Typography>
      <Typography variant="h6" className={classes.contentHeading}>
        FAQs about IntelliGenie
      </Typography>
      <div className={classes.FAQsContainer}>
        {faqs.length > 0 ? (
          faqs.map((ele) => {
            return (
              <Accordion
                key={ele._id}
                className={classes.accordion}
                expanded={expanded === "panel1"}
                onChange={handleChange("panel1")}
              >
                <AccordionSummary
                  expandIcon={<ExpandMoreIcon color="primary" />}
                  aria-controls="panel1bh-content"
                  id="panel1bh-header"
                >
                  <Typography className={classes.accordionHeading}>
                    {ele.question}
                  </Typography>
                </AccordionSummary>
                <AccordionDetails>
                  <Typography>{ele.answer}</Typography>
                </AccordionDetails>
              </Accordion>
            );
          })
        ) : (
          <CircularProgress size={30} color="primary" />
        )}
        <Accordion
          className={classes.accordion}
          expanded={expanded === "panel1"}
          onChange={handleChange("panel1")}
        >
          <AccordionSummary
            expandIcon={<ExpandMoreIcon color="primary" />}
            aria-controls="panel1bh-content"
            id="panel1bh-header"
          >
            <Typography className={classes.accordionHeading}>
              Will my content be unique?
            </Typography>
          </AccordionSummary>
