import {
  Avatar,
  Button,
  TextField,
  CircularProgress,
  makeStyles,
  Box,
  Typography,
} from "@material-ui/core";
import BoxContainerWhite from "./BoxContainerWhite";
import { useState } from "react";
import { useForm } from "react-hook-form";
import Progress from "./ButtonLoader";
import Snackbar from "./Snackbar";

const useStyles = makeStyles((theme) => ({
  container: {
    paddingLeft: theme.spacing(3),
    width: "100%",
    [theme.breakpoints.down("sm")]: {
      marginTop: theme.spacing(4),
      marginBottom: theme.spacing(4),
      width: "90%",
      paddingLeft: theme.spacing(0),
    },
  },
  avatar: {
    width: "100px",
    height: "100px",
    backgroundColor: theme.palette.primary.main,
  },
  avatarText: {
    fontSize: "42px",
  },
  updateBtn: {
    backgroundImage: "linear-gradient(95.47deg, #0067B1 0.51%, #008181 99.64%)",
    boxShadow:
      "0px 3px 1px -2px rgba(0, 0, 0, 0.2), 0px 2px 2px rgba(0, 0, 0, 0.14), 0px 1px 5px rgba(0, 0, 0, 0.12)",
    color: theme.palette.light.main,
    marginTop: theme.spacing(2),
  },
  avatarContainer: {
    width: "30%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    [theme.breakpoints.down("sm")]: {
      width: "100%",
      marginBottom: theme.spacing(4),
    },
  },
  input: { display: "none" },
  content: {
    display: "flex",
    alignItems: "flex-start",
    [theme.breakpoints.down("sm")]: {
      flexDirection: "column",
    },
  },
  formContainer: {
    paddingLeft: theme.spacing(5),
    [theme.breakpoints.down("sm")]: {
      paddingLeft: theme.spacing(0),
    },
  },
  forms: {
    borderLeft: `1px solid ${theme.palette.primary.light}`,
    [theme.breakpoints.down("sm")]: {
      border: "none",
      textAlign: "center",
    },
  },
  formHeading: {
    fontSize: "24px",
    marginBottom: theme.spacing(4),
  },
  formHeading2: {
    fontSize: "24px",
    marginBottom: theme.spacing(4),
    marginTop: theme.spacing(6),
  },
  inputFeild: {
    marginBottom: theme.spacing(3),
  },
}));

function AdminDashboard() {
  const classes = useStyles();

  const {
    register,
    formState: { errors },
    handleSubmit,
  } = useForm();
  const { REACT_APP_BACKEND: BACKEND } = process.env;
  const [disable, setDisable] = useState(false);
  const [snackData, setSnackData] = useState({
    type: "",
    message: "",
    open: false,
  });
  return (
    <div className={classes.container}>
      <Snackbar
        open={snackData.open}
        type={snackData.type}
        message={snackData.message}
      />
      <BoxContainerWhite>
        <div className={classes.content}>
          <div className={classes.avatarContainer}>
            <Avatar className={classes.avatar}>
              <span className={classes.avatarText}> A </span>
            </Avatar>
            <input
              accept="image/*"
              className={classes.input}
              id="contained-button-file"
              type="file"
            />
            <label htmlFor="contained-button-file">
              <Button
                variant="contained"
                color="primary"
                component="span"
                className={classes.updateBtn}
              >
                Change Picture
              </Button>
            </label>
          </div>
          <div className={classes.forms}>
            <div className={classes.formContainer}>
              <Typography variant={"h5"} className={classes.formHeading}>
                Personal Information
              </Typography>
              <form
                noValidate
                onSubmit={handleSubmit((data) => {
                  // signIn(data);
                })}
              >
                <TextField
                  required
                  disabled
                  variant={"outlined"}
                  InputLabelProps={{ shrink: true }}
                  size={"medium"}
                  color={"primary"}
                  name="usename"
                  label={"Username"}
                  className={classes.inputFeild}
                  fullWidth
                  {...register("username", {
                    required: "Username Required",
                    pattern: {
                      value: /^[a-zA-Z]+[a-zA-Z0-9_.-]{2,}$/,
                      message: "Invalid Username",
                    },
                  })}
                  error={Boolean(errors.username)}
                  helperText={errors.username?.message}
                ></TextField>
                <TextField
                  required
                  variant={"outlined"}
                  InputLabelProps={{ shrink: true }}
                  size={"medium"}
                  color={"primary"}
                  label={"Password"}
                  name="password"
                  className={classes.inputFeild}
                  type={"password"}
                  fullWidth
                  {...register("password", {
                    required: "Password Required",
                    minLength: { value: 6, message: "Invalid Password" },
                  })}
                  error={Boolean(errors.password)}
                  helperText={errors.password?.message}
                ></TextField>
                <Box className={classes.btnGroup}>
                  <Box className={classes.btnGroupLeft}>
                    {/* <Link to={"/blogTitle"} className={classes.link}> */}
                    <Button
                      variant={"contained"}
                      color="primary"
                      size="large"
                      type="submit"
                      disabled={disable}
                    >
                      Sign In
                      {disable ? <Progress size={20} /> : ""}
                    </Button>
                    {/* </Link> */}
                  </Box>
                </Box>
              </form>
            </div>
            <div className={classes.formContainer}>
              <Typography variant={"h5"} className={classes.formHeading2}>
                Change Password
              </Typography>
              <TextField
                variant={"outlined"}
                color={"primary"}
                label={"Old Password"}
                type={"password"}
                className={classes.inputFeild}
                fullWidth
              ></TextField>
              <TextField
                variant={"outlined"}
                color={"primary"}
                type={"password"}
                label={"New Password"}
                className={classes.inputFeild}
                fullWidth
              ></TextField>
              <TextField
                variant={"outlined"}
                color={"primary"}
                type={"password"}
                label={"Confirm Password"}
                className={classes.inputFeild}
                fullWidth
              ></TextField>
              <Button className={classes.updateBtn} variant={"contained"}>
                Update Password
              </Button>
            </div>
          </div>
        </div>
      </BoxContainerWhite>
    </div>
  );
}
export default AdminDashboard;
