import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import StepContent from "@material-ui/core/StepContent";
import Button from "@material-ui/core/Button";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import SignInUpTemplate from "./SignInUpTemplate";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
  },
  button: {
    marginTop: theme.spacing(1),
    marginRight: theme.spacing(1),
  },
  actionsContainer: {
    marginBottom: theme.spacing(2),
  },
  resetContainer: {
    padding: theme.spacing(3),
  },
}));

function getSteps() {
  return ["Verify Email", "Verify OTP", "Change Password"];
}

export default function ForgetPassword() {
  const classes = useStyles();
  function getStepContent(step) {
    switch (step) {
      case 0:
        return (
          <form
            noValidate
            onSubmit={handleSubmit((data) => {
              // signIn(data);
            })}
          >
            <TextField
              required
              disabled
              variant={"outlined"}
              InputLabelProps={{ shrink: true }}
              size={"medium"}
              color={"primary"}
              name="usename"
              label={"Username"}
              className={classes.inputFeild}
              fullWidth
              {...register("username", {
                required: "Username Required",
                pattern: {
                  value: /^[a-zA-Z]+[a-zA-Z0-9_.-]{2,}$/,
                  message: "Invalid Username",
                },
              })}
              error={Boolean(errors.username)}
              helperText={errors.username?.message}
            ></TextField>
            <TextField
              variant={"outlined"}
              InputLabelProps={{ shrink: true }}
              size={"medium"}
              color={"primary"}
              label={"Email"}
              name="userEmail"
              className={classes.inputFeild}
              {...register("userEmail", {
                required: "Email required",
                pattern: {
                  value:
                    /^[a-zA-Z]+[a-zA-Z0-9_.-]{1,}\@([A-Za-z0-9_\-\.]){1,}\.([A-Za-z]){2,4}$/,
                  message: "Invalid email",
                },
              })}
              error={Boolean(errors.userEmail)}
              helperText={errors.userEmail?.message}
              fullWidth
            ></TextField>
            <Box className={classes.btnGroup}>
              <Box className={classes.btnGroupLeft}>
                <Button
                  variant={"contained"}
                  disableElevation
                  color="primary"
                  size="large"
                  type="submit"
                  disabled={disable}
                >
                  Update Information
                  {disable ? <Progress size={20} /> : ""}
                </Button>
              </Box>
            </Box>
          </form>
        );
      case 1:
        return "An ad group contains one or more ads which target a shared set of keywords.";
      case 2:
        return `Try out different ad text to see what brings in the most customers,
                and learn how to enhance your ads using features like ad extensions.
                If you run into any problems with your ads, find out how to tell if
                they're running and how to resolve approval issues.`;
      default:
        return "Unknown step";
    }
  }
  const {
    register,
    formState: { errors },
    handleSubmit,
    setValue,
  } = useForm();
  const [activeStep, setActiveStep] = React.useState(0);
  const steps = getSteps();

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  const handleReset = () => {
    setActiveStep(0);
  };

  return (
    <SignInUpTemplate>
      <div className={classes.root}>
        <Stepper activeStep={activeStep} orientation="vertical">
          {steps.map((label, index) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
              <StepContent>
                <Typography>{getStepContent(index)}</Typography>
                <div className={classes.actionsContainer}>
                  <div>
                    <Button
                      disabled={activeStep === 0}
                      onClick={handleBack}
                      className={classes.button}
                    >
                      Back
                    </Button>
                    <Button
                      variant="contained"
                      color="primary"
                      onClick={handleNext}
                      className={classes.button}
                    >
                      {activeStep === steps.length - 1 ? "Finish" : "Next"}
                    </Button>
                  </div>
                </div>
              </StepContent>
            </Step>
          ))}
        </Stepper>
        {activeStep === steps.length && (
          <Paper square elevation={0} className={classes.resetContainer}>
            <Typography>All steps completed - you&apos;re finished</Typography>
            <Button onClick={handleReset} className={classes.button}>
              Reset
            </Button>
          </Paper>
        )}
      </div>
    </SignInUpTemplate>
  );
}
