import {
  Button,
  Card,
  makeStyles,
  TextField,
  Typography,
  IconButton,
  CircularProgress,
} from "@material-ui/core";
import { MdDelete, MdEdit } from "react-icons/md";
import { Autocomplete } from "@material-ui/lab";
import Rating from "@material-ui/lab/Rating";
import React, { useState, useEffect } from "react";
import Snackbar from "./Snackbar";
import axios from "axios";
import { useForm } from "react-hook-form";

const useStyles = makeStyles((theme) => ({
  content: {
    width: "100%",
    boxSizing: "border-box",
    margin: theme.spacing(7),
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
    display: "flex",
    flexDirection: "column",

    [theme.breakpoints.down("md")]: {
      margin: theme.spacing(7),
      width: "100%",
    },

    [theme.breakpoints.down("sm")]: {
      margin: theme.spacing(2),
    },
  },
  heading: {
    textAlign: "center",
    color: theme.palette.primary.main,
    fontWeight: "bold",
    fontSize: "1.5rem",
    marginBottom: theme.spacing(4),
  },
  ratingFeedbackHead: {
    padding: theme.spacing(1),
    color: theme.palette.primary.main,
  },
  complaintCard: {
    padding: theme.spacing(4),
    margin: theme.spacing(1),
    boxSizing: "border-box",
    boxShadow: "none",
    border: `1px solid ${theme.palette.primary.light}`,
    width: "98.5%",
    [theme.breakpoints.down("xs")]: {
      width: "95%",
      padding: theme.spacing(3),
    },
  },
  catagoryHeading: {
    fontSize: "14px",
    marginBottom: theme.spacing(1),
    fontWeight: "bold",
    color: theme.palette.dark.main,
  },

  complaintHeading: {
    color: theme.palette.dark.main,
    fontSize: "14px",
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2),
    fontWeight: "bold",
  },
  submitBtn: {
    marginTop: theme.spacing(2),
    backgroundImage: `linear-gradient(95.47deg, ${theme.palette.secondary.main} 0%,  ${theme.palette.primary.main} 100%)`,
    color: theme.palette.light.main,
    paddingRight: theme.spacing(2),
    paddingLeft: theme.spacing(2),
  },
  complainCategory: {
    marginTop: theme.spacing(1),
  },
}));

function Complaint() {
  const classes = useStyles();
  const complainCategory = [
    {
      title: "Information Update",
    },
    {
      title: "Payment and subscription",
    },
    {
      title: "Broken interface",
    },
    {
      title: "Content related issue",
    },
    {
      title: "Issue after update",
    },
    {
      title: "Other",
    },
  ];

  const [actionID, setActionID] = useState("");

  const { REACT_APP_BACKEND: BACKEND } = process.env;

  const [snackData, setSnackData] = useState({
    type: "",
    message: "",
    open: false,
  });

  const [myComplaints, setMyComplaints] = useState([]);
  const [pageLoading, setPageLoading] = useState(true);

  //   Edit Feedback Modal methods
  const [editComplaint, seteditComplaint] = useState(false);

  const editOpen = () => {
    seteditComplaint(true);
  };

  const editClose = () => {
    seteditComplaint(false);
  };

  const [deleteComplaint, setdeleteComplaint] = useState(false);

  const deleteOpen = () => {
    setdeleteComplaint(true);
  };

  const deleteClose = () => {
    setdeleteComplaint(false);
  };

  const {
    register: register1,
    formState: { errors: errors1 },
    handleSubmit: handleSubmit1,
    setValue,
  } = useForm();

  const {
    register,
    formState: { errors },
    handleSubmit,
    reset,
  } = useForm();

  function getmyComplaints() {
    var config = {
      method: "get",
      url: `${BACKEND}/userRouter/viewAllComplaints${
        JSON.parse(localStorage.getItem("user"))._id
      }`,
      headers: {
        Authorization: localStorage.getItem("token"),
        "Content-Type": "application/json",
      },
    };

    axios(config)
      .then(function (response) {
        setMyComplaints(response.data);
        setPageLoading(false);
      })
      .catch(function (error) {
        console.log(error);
        setSnackData({
          type: "error",
          message: "Error getting Complaints",
          open: true,
        });
      });
  }

  useEffect(() => {
    getmyComplaints();
  }, []);

  return (
    <div className={classes.content}>
      <Snackbar
        open={snackData.open}
        type={snackData.type}
        message={snackData.message}
      />
      <Typography variant="h6" component={"h1"} className={classes.heading}>
        COMPLAINTS AND RESPONSES
      </Typography>
      <Typography
        variant="h6"
        component={"h2"}
        className={classes.ratingFeedbackHead}
      >
        Your Complaint
      </Typography>
      <Card className={classes.complaintCard}>
        <Typography
          variant="h6"
          component={"h6"}
          className={classes.catagoryHeading}
        >
          Select Category
        </Typography>
        <Autocomplete
          id="combo-box-demo"
          options={complainCategory}
          getOptionLabel={(option) => option.title}
          style={{ width: 300 }}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Issue Category"
              variant="outlined"
              className={classes.complainCategory}
              InputLabelProps={{ shrink: true }}
              required
            />
          )}
        />

        <Typography
          variant="h6"
          component={"h6"}
          className={classes.complaintHeading}
        >
          Please Provide Feedback
        </Typography>
        <TextField
          required
          label={"Complaint"}
          variant={"outlined"}
          multiline
          fullWidth
          rows={8}
          InputLabelProps={{ shrink: true }}
        />
        <Button className={classes.submitBtn}>Register Complaint</Button>
      </Card>
      <Typography
        variant="h6"
        component={"h2"}
        className={classes.ratingsFeedbacksHeading}
      >
        Your Complaints
      </Typography>
      <div className={classes.ratingsFeedbacksContainer}>
        {myComplaints.length > 0 ? (
          myComplaints.map((ele) => {
            return (
              <Card className={classes.feedbackCard}>
                <div className={classes.dFlexBetween}>
                  <div>
                    <Typography
                      variant="h6"
                      component={"h3"}
                      className={classes.cardName}
                    >
                      {ele.userFullName}
                    </Typography>
                    <Rating
                      name="simple-controlled"
                      size="small"
                      value={ele.review}
                      disabled
                      className={classes.cardRating}
                    />
                  </div>
                  <div>
                    <IconButton
                      color="primary"
                      aria-label="edit"
                      onClick={() => {
                        setActionID(ele._id);
                        setValue("feedback", ele.feedback);
                        editOpen();
                      }}
                    >
                      <MdEdit color="primary" />
                    </IconButton>
                    <IconButton
                      color="primary"
                      aria-label="delete"
                      onClick={() => {
                        setActionID(ele._id);
                        deleteOpen();
                      }}
                    >
                      <MdDelete />
                    </IconButton>
                  </div>
                </div>
                <Typography
                  variant="body1"
                  component={"p"}
                  className={classes.cardFeedback}
                >
                  {ele.feedback}
                </Typography>
                <div className={classes.reply}>
                  {ele.reply ? (
                    <div className={classes.p2}>
                      <Typography
                        variant="body1"
                        component={"p"}
                        color="primary"
                        className={classes.cardFeedback}
                      >
                        Admin
                      </Typography>
                      <Typography variant="body1" component={"p"}>
                        {ele.reply}
                      </Typography>
                    </div>
                  ) : (
                    ""
                  )}
                </div>
                <Typography
                  variant="body1"
                  component={"p"}
                  className={classes.cardTag}
                >
                  Powered By IntelliGenie
                </Typography>
              </Card>
            );
          })
        ) : (
          <div className={classes.displayFlexCenter}>
            {pageLoading ? (
              <CircularProgress size={30} color="primary" />
            ) : (
              <p>No Complaints To Show</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default Complaint;
