import {
  Button,
  Card,
  makeStyles,
  TextField,
  Typography,
} from "@material-ui/core";
import { Autocomplete } from "@material-ui/lab";
import Rating from "@material-ui/lab/Rating";
import React from "react";

const useStyles = makeStyles((theme) => ({
  content: {
    width: "100%",
    boxSizing: "border-box",
    margin: theme.spacing(7),
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
    display: "flex",
    flexDirection: "column",

    [theme.breakpoints.down("md")]: {
      margin: theme.spacing(7),
      width: "100%",
    },

    [theme.breakpoints.down("sm")]: {
      margin: theme.spacing(2),
    },
  },
  heading: {
    textAlign: "center",
    color: theme.palette.primary.main,
    fontWeight: "bold",
    fontSize: "1.5rem",
    marginBottom: theme.spacing(4),
  },
  ratingFeedbackHead: {
    padding: theme.spacing(1),
    color: theme.palette.primary.main,
  },
  complaintCard: {
    padding: theme.spacing(4),
    margin: theme.spacing(1),
    boxSizing: "border-box",
    boxShadow: "none",
    border: `1px solid ${theme.palette.primary.light}`,
    width: "98.5%",
    [theme.breakpoints.down("xs")]: {
      width: "95%",
      padding: theme.spacing(3),
    },
  },
  catagoryHeading: {
    fontSize: "14px",
    marginBottom: theme.spacing(1),
    fontWeight: "bold",
    color: theme.palette.dark.main,
  },

  complaintHeading: {
    color: theme.palette.dark.main,
    fontSize: "14px",
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2),
    fontWeight: "bold",
  },
  submitBtn: {
    marginTop: theme.spacing(2),
    backgroundImage: `linear-gradient(95.47deg, ${theme.palette.secondary.main} 0%,  ${theme.palette.primary.main} 100%)`,
    color: theme.palette.light.main,
    paddingRight: theme.spacing(2),
    paddingLeft: theme.spacing(2),
  },
  complainCategory: {
    marginTop: theme.spacing(1),
  },
}));

function Complaint() {
  const classes = useStyles();
  const complainCategory = [
    {
      title: "Information Update",
    },
    {
      title: "Payment and subscription",
    },
    {
      title: "Broken interface",
    },
    {
      title: "Content related issue",
    },
    {
      title: "Issue after update",
    },
    {
      title: "Other",
    },
  ];

  return (
    <div className={classes.content}>
      <Typography variant="h6" component={"h1"} className={classes.heading}>
        COMPLAINTS AND RESPONSES
      </Typography>
      <Typography
        variant="h6"
        component={"h2"}
        className={classes.ratingFeedbackHead}
      >
        Your Complaint
      </Typography>
      <Card className={classes.complaintCard}>
        <Typography
          variant="h6"
          component={"h6"}
          className={classes.catagoryHeading}
        >
          Select Category
        </Typography>
        <Autocomplete
          id="combo-box-demo"
          options={complainCategory}
          getOptionLabel={(option) => option.title}
          style={{ width: 300 }}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Issue Category"
              variant="outlined"
              className={classes.complainCategory}
              InputLabelProps={{ shrink: true }}
              required
            />
          )}
        />

        <Typography
          variant="h6"
          component={"h6"}
          className={classes.complaintHeading}
        >
          Please Provide Feedback
        </Typography>
        <TextField
          required
          label={"Complaint"}
          variant={"outlined"}
          multiline
          fullWidth
          rows={8}
          InputLabelProps={{ shrink: true }}
        />
        <Button className={classes.submitBtn}>Register Complaint</Button>
      </Card>
    </div>
  );
}

export default Complaint;
