import {
  Button,
  Card,
  makeStyles,
  TextField,
  Typography,
} from "@material-ui/core";
import Rating from "@material-ui/lab/Rating";
import Progress from "./ButtonLoader";
import React, { useState } from "react";

const useStyles = makeStyles((theme) => ({
  content: {
    width: "100%",
    boxSizing: "border-box",
    margin: theme.spacing(7),
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
    display: "flex",
    flexDirection: "column",

    [theme.breakpoints.down("md")]: {
      margin: theme.spacing(7),
      width: "100%",
    },

    [theme.breakpoints.down("sm")]: {
      margin: theme.spacing(2),
    },
  },
  heading: {
    textAlign: "center",
    color: theme.palette.primary.main,
    fontWeight: "bold",
    fontSize: "1.5rem",
    marginBottom: theme.spacing(4),
  },
  ratingFeedbackHead: {
    padding: theme.spacing(1),
    color: theme.palette.primary.main,
  },
  ratingFeedbackCard: {
    padding: theme.spacing(4),
    margin: theme.spacing(1),
    boxSizing: "border-box",
    boxShadow: "none",
    border: `1px solid ${theme.palette.primary.light}`,
    width: "98.5%",
    [theme.breakpoints.down("xs")]: {
      width: "95%",
      padding: theme.spacing(3),
    },
  },
  ratingHeading: {
    fontSize: "14px",
    marginBottom: theme.spacing(1),
    fontWeight: "bold",
    color: theme.palette.dark.main,
  },
  ratingStars: {
    transform: "scale(1.5)",
  },
  feedbackHeading: {
    color: theme.palette.dark.main,
    fontSize: "14px",
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2),
    fontWeight: "bold",
  },
  submitBtn: {
    marginTop: theme.spacing(2),
    color: theme.palette.light.main,
    paddingRight: theme.spacing(2),
    paddingLeft: theme.spacing(2),
  },
  ratingsFeedbacksHeading: {
    marginTop: theme.spacing(4),
    padding: theme.spacing(1),
    color: theme.palette.primary.main,
  },
  ratingsFeedbacksContainer: {
    maxWidth: "100%",
    display: "flex",
    flexWrap: "wrap",
  },
  feedbackCard: {
    flexGrow: 1,
    width: "300px",
    minHeight: "200px",
    boxShadow: "none",
    margin: theme.spacing(1),
    padding: theme.spacing(4),
    border: `1px solid ${theme.palette.primary.light}`,
  },
  cardName: {
    fontSize: "16px",
    fontWeight: "bold",
    color: theme.palette.primary.main,
  },
  cardRating: {
    marginTop: theme.spacing(1),
  },
  cardFeedback: {
    marginTop: theme.spacing(1),
  },
  cardTag: {
    display: "inline-block",
    fontWeight: "bold",
    fontFamily: "Cookie, cursive",
    fontSize: "20px",
    marginTop: theme.spacing(2),
    backgroundImage: `linear-gradient(45deg, ${theme.palette.primary.main}, ${theme.palette.secondary.main})`,
    backgroundSize: "100%",
    WebkitBackgroundClip: "text",
    MozBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    MozTextFillColor: "transparent",
  },
  dFlexBetween: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
}));

function RatingFeedback() {
  const classes = useStyles();
  const [rating, setRating] = useState(0);
  const [feedback, setFeedback] = useState("");
  const [disable, setDisable] = useState(false);

  function postFeedback() {
    setDisable(true);
  }

  return (
    <div className={classes.content}>
      <Typography variant="h6" component={"h1"} className={classes.heading}>
        RATINGS AND FEEDBACKS
      </Typography>
      <Typography
        variant="h6"
        component={"h2"}
        className={classes.ratingFeedbackHead}
      >
        Your Feedback
      </Typography>
      <Card className={classes.ratingFeedbackCard}>
        <Typography
          variant="h6"
          component={"h6"}
          className={classes.ratingHeading}
        >
          Please Rate Us
        </Typography>
        <Rating
          name="simple-controlled"
          size="large"
          value={rating}
          onChange={(_, val) => setRating(val)}
        />
        <Typography
          variant="h6"
          component={"h6"}
          className={classes.feedbackHeading}
        >
          Please Provide Feedback
        </Typography>
        <TextField
          variant={"outlined"}
          multiline
          fullWidth
          rows={8}
          InputLabelProps={{ shrink: true }}
          value={feedback}
          onChange={(_, val) => setFeedback(val)}
        />
        <Button
          className={classes.submitBtn}
          variant={"contained"}
          onClick={postFeedback}
          disabled={disable}
          color={"primary"}
        >
          Submit Feedback
          {disable ? <Progress color="primary" size={20} /> : ""}
        </Button>
      </Card>
      <Typography
        variant="h6"
        component={"h2"}
        className={classes.ratingsFeedbacksHeading}
      >
        Your Feedbacks
      </Typography>
      <div className={classes.ratingsFeedbacksContainer}>
        <Card className={classes.feedbackCard}>
          <div className={classes.dFlexBetween}>
            <div>
              <Typography
                variant="h6"
                component={"h3"}
                className={classes.cardName}
              >
                Muhammad Yousaf
              </Typography>
              <Rating
                name="simple-controlled"
                size="small"
                value={5}
                disabled
                className={classes.cardRating}
              />
            </div>
            <div>
              <Button>Edit</Button>
            </div>
          </div>
          <Typography
            variant="body1"
            component={"p"}
            className={classes.cardFeedback}
          >
            Adrien Brody and Milla Jovovich, "Dummy" is that special kind of
            indie comedy that knows how to perfectly balance its offbeat humor
            with just the right amount of dramatic intrigue.
          </Typography>
          <Typography
            variant="body1"
            component={"p"}
            className={classes.cardTag}
          >
            Powered By IntelliGenie
          </Typography>
        </Card>
      </div>
      <Typography
        variant="h6"
        component={"h2"}
        className={classes.ratingsFeedbacksHeading}
      >
        Other People Feedbacks
      </Typography>
      <div className={classes.ratingsFeedbacksContainer}>
        <Card className={classes.feedbackCard}>
          <Typography
            variant="h6"
            component={"h3"}
            className={classes.cardName}
