import {
  Button,
  Card,
  makeStyles,
  TextField,
  Typography,
} from "@material-ui/core";
import { Link } from "react-router-dom";
import { useState, useRef } from "react";
import { useForm } from "react-hook-form";

const useStyles = makeStyles((theme) => ({
  container: {
    width: "100vw",
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: theme.palette.primary.light,
  },
  formContainer: {
    padding: theme.spacing(5),
    minWidth: "350px",
    maxWidth: "400px",
    borderRadius: theme.shape.borderRadius,
  },
  formHeading: {
    fontWeight: "bold",
    textAlign: "center",
    marginBottom: theme.spacing(3),
  },
  formItem: {
    marginBottom: theme.spacing(2),
  },
  btn: {
    padding: theme.spacing(1.5),
    marginBottom: theme.spacing(2),
  },
  link: {
    textDecoration: "none",
    color: theme.palette.primary.main,
  },
}));

function AdminSignup() {
  const classes = useStyles();
  const { REACT_APP_BACKEND: BACKEND } = process.env;
  const {
    register,
    formState: { errors },
    handleSubmit,
    watch,
    reset,
    setValue,
  } = useForm();

  const [disable, setDisable] = useState(false);
  const [snackData, setSnackData] = useState({
    type: "",
    message: "",
    open: false,
  });

  const password = useRef({});
  password.current = watch("password", "");

  return (
    <div className={classes.container}>
      <Card className={classes.formContainer}>
        <form
          noValidate
          onSubmit={handleSubmit((data) => {
            signUp(data);
          })}
        >
          <TextField
            variant={"outlined"}
            InputLabelProps={{ shrink: true }}
            size={"medium"}
            color={"primary"}
            label={"First Name"}
            name="firstName"
            className={classes.inputFeild}
            {...register("firstName", {
              required: "First name required",
              pattern: {
                value: /^[a-zA-Z ]*$/,
                message: "Invalid first name",
              },
            })}
            error={Boolean(errors.firstName)}
            helperText={errors.firstName?.message}
            fullWidth
          ></TextField>
          <TextField
            variant={"outlined"}
            InputLabelProps={{ shrink: true }}
            size={"medium"}
            color={"primary"}
            label={"Last Name"}
            className={classes.inputFeild}
            name="lastName"
            {...register("lastName", {
              required: "Last name required",
              pattern: {
                value: /^[a-zA-Z ]*$/,
                message: "Invalid last name",
              },
            })}
            error={Boolean(errors.lastName)}
            helperText={errors.lastName?.message}
            fullWidth
          ></TextField>
          <TextField
            variant={"outlined"}
            InputLabelProps={{ shrink: true }}
            size={"medium"}
            color={"primary"}
            label={"Email"}
            name="userEmail"
            className={classes.inputFeild}
            {...register("userEmail", {
              required: "Email required",
              pattern: {
                value:
                  /^[a-zA-Z]+[a-zA-Z0-9_.-]{1,}\@([A-Za-z0-9_\-\.]){1,}\.([A-Za-z]){2,4}$/,
                message: "Invalid email",
              },
            })}
            error={Boolean(errors.userEmail)}
            helperText={errors.userEmail?.message}
            fullWidth
          ></TextField>
          <TextField
            variant={"outlined"}
            InputLabelProps={{ shrink: true }}
            size={"medium"}
            color={"primary"}
            label={"Username"}
            name="username"
            className={classes.inputFeild}
            {...register("username", {
              required: "Username Required",
              pattern: {
                value: /^[a-zA-Z]+[a-zA-Z0-9_.-]{2,}$/,
                minLength: {
                  value: 6,
                  message: "Username can not be less than 3 characters",
                },
                message: "Invalid Username",
              },
            })}
            error={Boolean(errors.username)}
            helperText={errors.username?.message}
            fullWidth
          ></TextField>
          <TextField
            variant={"outlined"}
            InputLabelProps={{ shrink: true }}
            size={"medium"}
            color={"primary"}
            label={"Password"}
            className={classes.inputFeild}
            name="password"
            {...register("password", {
              required: "Password Required",
              minLength: { value: 6, message: "Invalid Password" },
            })}
            type={"password"}
            fullWidth
            error={Boolean(errors.password)}
            helperText={errors.password?.message}
          ></TextField>
          <TextField
            variant={"outlined"}
            InputLabelProps={{ shrink: true }}
            size={"medium"}
            color={"primary"}
            label={"Confirm Password"}
            className={classes.inputFeild}
            name="confirmPassword"
            {...register("confirmPassword", {
              required: "Confirm Password Required",
              validate: (value) =>
                value === password.current || "Passwords do not match",
            })}
            type={"password"}
            error={Boolean(errors.confirmPassword)}
            helperText={errors.confirmPassword?.message}
            fullWidth
          ></TextField>
          <Box className={classes.btnGroup}>
            <Button
              variant={"contained"}
              color="primary"
              size="large"
              type="submit"
              disabled={disable}
            >
              Sign up
              {disable ? <Progress color="primary" size={20} /> : ""}
            </Button>
            <Box className={classes.socialGroup}>
              <IconButton
                aria-label={"Login with Facebook"}
                className={classes.iconButton}
              >
                <Facebook className={classes.icon} />
              </IconButton>
              <IconButton
                aria-label={"Login with Google"}
                className={classes.iconButton}
              >
                <Google className={classes.icon} />
              </IconButton>
              <IconButton
                aria-label={"Login with Twitter"}
                className={classes.iconButton}
              >
                <Twitter className={classes.icon} />
              </IconButton>
              <IconButton
                aria-label={"Login with GitHub"}
                className={classes.iconButton}
              >
                <GitHub className={classes.icon} />
              </IconButton>
            </Box>
          </Box>
          <Typography
            variant={"body2"}
            component={"span"}
            className={classes.loginLink}
          >
            Alread Registered?{" "}
            <Link to={"/signin"} className={classes.anchorTag}>
              Log In
            </Link>
          </Typography>
        </form>
      </Card>
    </div>
  );
}
export default AdminSignup;
